import React, { Component } from 'react'
import { Button, Card } from 'react-bootstrap'
import moment from 'moment'
import DatePicker from 'react-datepicker'
import '../../vendor/libs/react-datepicker/react-datepicker.scss'
import Flatpickr from 'react-flatpickr'
import '../../vendor/libs/react-flatpickr/react-flatpickr.scss'
import {
  AlphaPicker,
  BlockPicker,
  ChromePicker,
  CirclePicker,
  CompactPicker,
  GithubPicker,
  HuePicker,
  MaterialPicker,
  PhotoshopPicker,
  SketchPicker,
  SliderPicker,
  SwatchesPicker,
  TwitterPicker
} from 'react-color'

// react-datepicker custom input
//

class ExampleCustomInput extends Component {
  render() {
    return (
      <Button onClick={this.props.onClick}>
        {this.props.value}
      </Button>
    )
  }
}

//

class Pickers extends Component {
  constructor(props) {
    super(props)
    props.setTitle('Pickers - Forms')

    this.state = {
      // react-datepicker state
      //

      startDate: new Date(),
      endDate: moment().add(5, 'days').toDate(),

      // react-flatpickr state
      //

      date: null,
      time: null,
      datetime: null,
      full: null,
      range: null,
      inline: null,

      // react-color state
      //

      color: '#A4DD00'
    }

    // react-datepicker bindings
    //

    this.handleChange = this.handleChange.bind(this)
    this.handleChangeStart = this.handleChangeStart.bind(this)
    this.handleChangeEnd = this.handleChangeEnd.bind(this)

    // react-color bindings
    //

    this.handleChangeComplete = this.handleChangeComplete.bind(this)

  }

  // react-datepicker methods
  //

  setDates({ startDate = this.state.startDate, endDate = this.state.endDate }) {
    if (moment(startDate).isAfter(endDate)) {
      endDate = startDate
    }

    this.setState({
      startDate,
      endDate
    })
  }

  handleChange(date) {
    this.setDates({ startDate: date })
  }

  handleChangeStart(startDate) {
    this.setDates({ startDate })
  }

  handleChangeEnd(endDate) {
    this.setDates({ endDate })
  }

  get placement() {
    const isRTL = document.documentElement.getAttribute('dir') === 'rtl'
    return isRTL ? 'auto-end' : 'auto-start'
  }

  // react-color methods
  //

  handleChangeComplete(color) {
    this.setState({
      color: color.rgb
    })
  }

  //

  render() {
    const isIEMode = typeof document['documentMode'] === 'number'
    const isIE10Mode = document['documentMode'] === 10
    const isRTL = document.documentElement.getAttribute('dir') === 'rtl'

    // react-flatpickr config
    //

    const dateConfig = {
      altInput: true,
      animate: !isRTL
    }
    const timeConfig = {
      enableTime: true,
      noCalendar: true,
      altInput: true,
      animate: !isRTL
    }
    const datetimeConfig = {
      enableTime: true,
      altInput: true,
      animate: !isRTL
    }
    const fullConfig = {
      weekNumbers: true,
      enableTime: true,
      mode: 'multiple',
      minDate: 'today',
      altInput: true,
      animate: !isRTL
    }
    const rangeConfig = {
      mode: 'range',
      altInput: true,
      animate: !isRTL
    }
    const inlineConfig = {
      inline: true,
      altInput: true,
      allowInput: false,
      animate: !isRTL
    }

    return (
      <div>
        <h4 className="font-weight-bold py-3 mb-4">
          <span className="text-muted font-weight-light">Forms /</span> Pickers
        </h4>

        {isIE10Mode && <div className="alert alert-danger">
          <strong>react-datepicker</strong> doesn't support Internet Explorer 10
        </div>}

        {!isIE10Mode && <React.Fragment>
          <Card className="mb-4">
            <Card.Header as="h6">react-datepicker</Card.Header>
            <Card.Body>
              <DatePicker className="form-control"
                selected={this.state.startDate}
                onChange={this.handleChange}
                popperPlacement={this.placement}
              />
            </Card.Body>
            <hr className="m-0" />
            <Card.Body>
              <div className="text-light small font-weight-semibold mb-3">Custom</div>

              <DatePicker className="form-control"
                dateFormat="MM/dd/yyyy"
                placeholderText="Click to select a date"
                todayButton={"Today"}
                highlightDates={[moment().subtract(2, 'days').toDate(), moment().add(3, 'days').toDate()]}
                showWeekNumbers
                isClearable={true}
                selected={this.state.startDate}
                onChange={this.handleChange}
                popperPlacement={this.placement}
              />
            </Card.Body>
            <hr className="m-0" />
            <Card.Body>
              <div className="text-light small font-weight-semibold mb-3">Datetime</div>

              <div className="demo-vertical-spacing-sm">
                <div>
                  <DatePicker className="form-control"
                    calendarClassName="react-datepicker--with-time"
                    selected={this.state.startDate}
                    onChange={this.handleChange}
                    showTimeSelect
                    timeFormat="HH:mm"
                    timeIntervals={15}
                    dateFormat="MMMM d, yyyy h:mm aa"
                    timeCaption="Time"
                    popperPlacement={this.placement}
                  />
                </div>
                <div>
                  <DatePicker className="form-control"
                    calendarClassName="react-datepicker--with-time"
                    todayButton={"Today"}
                    selected={this.state.startDate}
                    onChange={this.handleChange}
                    showTimeSelect
                    timeFormat="HH:mm"
                    timeIntervals={15}
                    dateFormat="MMMM d, yyyy h:mm aa"
                    timeCaption="Time"
                    popperPlacement={this.placement}
                  />
                </div>

                <div>
                  {/* Time input */}
                  <DatePicker className="form-control"
                    selected={this.state.startDate}
                    timeInputLabel="Time:"
                    onChange={this.handleChange}
                    dateFormat="MM/dd/yyyy h:mm aa"
                    showTimeInput
                    popperPlacement={this.placement}
                  />
                </div>
              </div>
            </Card.Body>
            <hr className="m-0" />
            <Card.Body>
              <div className="text-light small font-weight-semibold mb-3">Time</div>

              <DatePicker className="form-control"
                selected={this.state.startDate}
                onChange={this.handleChange}
                showTimeSelect
                showTimeSelectOnly
                timeIntervals={15}
                dateFormat="h:mm aa"
                timeCaption="Time"
                popperPlacement={this.placement}
