                <div className="text-muted small">Items</div>
                {this.orderItems}
              </Col>
              <Col md={4} className="mb-3">
                <div className="text-muted small">Amount</div>
                {this.orderAmount}
              </Col>
            </Row>
          </Card.Body>
          <hr className="m-0" />
          {/* / Info */}

          {/* Billing */}
